//MARK: - Table view datasource and delegate func
extension TermsConditionsVC: UITableViewDataSource {
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        array_titles.count
    }
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: cell_identifier, for: indexPath) as! TermsConditionsTVCell
        cell.label_Title.text = array_titles[indexPath.row]
        cell.label_description.text = array_data[indexPath.row]
        return cell
    }
}
extension TermsConditionsVC: UITableViewDelegate {
    
    func tableView(_ tableView: UITableView, heightForRowAt indexPath: IndexPath) -> CGFloat {
        return UITableView.automaticDimension
    }
